import "./Login.css";
import { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getLogin } from "../../services/API";
import { getToken } from "../../features/Token/token";
import { Navigate } from "react-router-dom";


function Login() {
    // Use State
    let [loginErreur, setLoginErreur] = useState("");
    let [loginStatus, setLoginStatus] = useState(0);

    let [email, setEmail] = useState("");
    let [password, setPassword] = useState("");
    let [remember, setRemember] = useState(false);


    // Use Selector
    const token = useSelector((state) => state.token.value);


    // Use Effect
    // useEffect(() => {
    //     if(token === localStorage.getItem("token")) {
    //         ajoutToken(localStorage.getItem("token"));
    //     }
    // });
    useEffect(() => {
        const remembered = localStorage.getItem("remember-me") === "true";
        setRemember(remembered);

        if (remembered) {
            const storedToken = localStorage.getItem("token");
            if (storedToken) {
                ajoutToken(storedToken);
            }
        }
    }, []);


    // Handle Submit
    // const handleSubmit = (event) => {
    //     event.preventDefault();
    //     const login = getLogin({"email": email, "password": password});
    //     login.then(obj => {
    //         if(obj.status !== 400) {
    //             setLoginStatus(obj.status);
    //             ajoutToken(obj.token);
    //         } else {
    //             setLoginErreur(obj.message);
    //         }
    //     });
    // }
    const handleSubmit = async (event) => {
        event.preventDefault();
        const login = await getLogin({"email": email, "password": password});
        if (login && login.status !== 400) {
            setLoginStatus(login.status);
            ajoutToken(login.token);
        } else {
            setLoginErreur(login.message);
        }
    }


    // Handle Remember
    // const handleRemember = (event) => {
    //     setRemember(event.target.checked);
    // }
    const handleRemember = (event) => {
        const checked = event.target.checked;
        setRemember(checked);

        // Enregistrement de l'état "Remember Me" dans le localStorage
        localStorage.setItem("remember-me", checked ? "true" : "false");
    }

    // Add the token
    const dispatch = useDispatch();
     const ajoutToken = (token) => {
         if(remember === true) {
             localStorage.setItem("token", token);
         }
         dispatch(getToken(token));
     }


    // Redirection
    if(token !== 0 || loginStatus === 200 || token === localStorage.getItem("token")) return <Navigate to="/profil" /> 

    return (
        <main className="bg-dark">
            <section className="sign-in-content">
                <i className="fa fa-user-circle sign-in-icon"></i>
                <h1>Sign In</h1>
                <form onSubmit={handleSubmit}>
                    <div className="input-wrapper">
                        <label htmlFor="username">Username</label>
                        <input type="text" id="username" onChange={e => setEmail(e.target.value)} />
                    </div>
                    <div className="input-wrapper">
                        <label htmlFor="password">Password</label>
                        <input type="password" id="password" onChange={e => setPassword(e.target.value)} />
                    </div>
                    <div className="input-remember">
                        <input type="checkbox" id="remember-me" onChange={handleRemember} />
                        <label htmlFor="remember-me">Remember me</label>
                    </div>
                    <div>
                        {loginErreur}
                    </div>
                    <button className="sign-in-button">Sign In</button>
                </form>
            </section>
        </main>
    );
}

export default Login;
